import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

import AddProduct from './AddProduct';

class ProductList extends Component {
  constructor(){
      super();
      this.state = { listOfProducts: [] };
  }

  getAllProducts = () =>{
    axios.get(`http://localhost:5000/api/products`, {withCredentials:true})
    .then(responseFromApi => {
      this.setState({
        listOfProducts: responseFromApi.data
      })
    })
  }

  componentDidMount() {
    this.getAllProducts();
  }

  render(){
    return(
    <div>
      <div>
        <AddProduct getData={() => this.getAllProducts()}/>
      </div>
        <tr>
          <th>Product Name</th>
          <th>Product Specification</th>
          <th>Measure</th>
          <th>Package</th>
        </tr>
        {this.state.listOfProducts.map((product, index) => {
          return (
          <div>
            <tr>
              <td key={product._id}><Link to={`/products/${product._id}`}>{product.name}</Link></td>
              <td>{product.specification}</td>
              <td>{product.measure}</td>
              <td>{product.packing}</td>
            </tr>
          </div>
          )})
          }
    </div>
    )
  }
}

export default ProductList;
