// navbar/Navbar.js
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import AuthService from './auth/auth-service';

class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = { loggedInUser: null };
    this.service = new AuthService();
  }

  componentWillReceiveProps(nextProps) {
    this.setState({...this.state, loggedInUser: nextProps["userInSession"]});
  }

  logoutUser = () =>{
    this.service.logout()
    .then(() => {
      this.setState({ loggedInUser: null });
      this.props.getUser(null);
    })
  }

  render(){
    if(this.state.loggedInUser){
      return(
      <div className='container-fluid'>
            <ul className="nav justify-content-center">
              <li className="nav-item nav-link"><a className="list-group-item list-group-item-action" href="/">Welcome: {this.state.loggedInUser.username}</a></li>
              <li className="nav-item nav-link"><a className="list-group-item list-group-item-action" href="/"><Link to='/products'>Products</Link></a></li>
              <li className="nav-item nav-link"><a className="list-group-item" href="/"><Link to='/'><button className='btn btn-primary mb-2' onClick={() => this.logoutUser()}>Logout</button></Link></a></li>
            </ul>
      </div>

      )
    } else {
      return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <a className="navbar-brand" href="/">shq</a>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item active">
                <Link to='/'>Login</Link>
              </li>
              <li className="nav-item active">
                <Link to='/signup'> Signup</Link>
              </li>
            </ul>
          </div>
        </nav>
      )
    }
  }
}

export default Navbar;
